mapper.select:

INNER JOIN (
  SELECT id FROM post
  WHERE COALESCE(modified_on, created_on) > #{dateTime}
  AND id IN (
      SELECT pt2.post_id FROM post_tag pt2

        WHERE pt2.tag_id=#{tagId}

  )
  ORDER BY COALESCE(modified_on, created_on) ASC, id ASC
  LIMIT #{limit}
) p2 ON p2.id=p.id
WHERE tag_id IS NOT NULL

    AND p.id IN (SELECT pt2.post_id FROM post_tag pt2 WHERE pt2.tag_id=#{tagId})

ORDER BY COALESCE(p.modified_on, p.created_on) DESC, p.id DESC, tag_name ASC


INNER JOIN (
    SELECT id FROM post
    WHERE COALESCE(modified_on, created_on) < #{dateTime}
    AND id IN (
        SELECT pt2.post_id FROM post_tag pt2

            WHERE pt2.tag_id=#{tagId}

    )
    ORDER BY COALESCE(modified_on, created_on) DESC, id DESC
    LIMIT #{limit}
) p2 ON p2.id=p.id
WHERE tag_id IS NOT NULL
ORDER BY COALESCE(p.modified_on, p.created_on) DESC, p.id DESC, tag_name ASC
