mapper.insert:
INSERT INTO web_log (
uuid
, session_id
, server_port
, request_uri
, query_string
, referer
, user_agent
, accept_encoding
, client_port
, client_ip
) VALUES (
UUID_TO_BIN(#{uuid}, true)
, #{sessionId}
, #{serverPort}
, #{requestUri}
, #{queryString}
, #{referer}
, #{userAgent}
, #{acceptEncoding}
, #{clientPort}
, INET6_ATON(#{clientIp})
)
